[centerX][centerY] = board[centerX][centerY + 1] = board[centerX + 1][centerY + 1] = tileToUse; trominoTiling(board, centerX, centerY, halfSize, currentRow, currentCol); // Lower left trominoTiling(board, xMissing, yMissing, halfSize, currentRow, centerY + 1); // Upper left trominoTiling(board, centerX + 1, centerY, halfSize, centerX + 1, currentCol); // Lower right trominoTiling(board, centerX + 1, centerY + 1, halfSize, centerX + 1, centerY + 1); // Upper right } else if (xMissing <= centerX && yMissing > centerY) { // Upper left quadrant board[centerX][centerY] = board[centerX + 1][centerY] = board[centerX + 1][centerY + 1] = tileToUse; trominoTiling(board, centerX, centerY, halfSize, currentRow, currentCol); // Lower left trominoTiling(board, centerX + 1, centerY, halfSize, currentRow, centerY + 1); // Upper left trominoTiling(board, xMissing, yMissing, halfSize, centerX + 1, currentCol); // Lower right trominoTiling(board, centerX + 1, centerY + 1, halfSize, centerX + 1, centerY + 1); // Upper right } else { // Upper right quadrant board[centerX][centerY] = board[centerX + 1][centerY] = board[centerX][centerY + 1] = tileToUse; trominoTiling(board, centerX, centerY, halfSize, currentRow, currentCol); // Lower left trominoTiling(board, centerX + 1, centerY, halfSize, currentRow, centerY + 1); // Upper left trominoTiling(board, centerX, centerY + 1, halfSize, centerX + 1, currentCol); // Lower right trominoTiling(board, xMissing, yMissing, halfSize, centerX + 1, centerY + 1); // Upper right } } // This initiates the recursion and prints the board using labeled trominoes public static void main(String[] args) { Scanner scanner = new Scanner(System.in); System.out.println(\\"Please enter size of board (need to be 2^n and 0 to quit):\\"); int boardSize = scanner.nextInt(); System.out.println(\\"Please enter coordinates of missing square (separated by a space):\\"); int xMissing = scanner.nextInt(); int yMissing = scanner.nextInt(); scanner.close(); int[][] board = ne